import { Link } from "react-router-dom";
import { useLocation } from "react-router-dom";

const About = () => {
  const location = useLocation();
  return (
    <div>
      <h4>Version 1.0.0</h4>
      {location.pathname === "/" && <Link to="/">Go Back</Link>}
    </div>
  );
};

export default About;
